def GetBalance(wallet_address, currency):
    #e.g currency = 'ether'

    #balanceOf = contract.functions.balanceOf(wallet_address).call()
    #result = (web3.fromWei(balanceOf, currency))

    wallet_address = Web3.toChecksumAddress(wallet_address)
    result = web3.eth.getBalance(wallet_address)

    result = web3.fromWei(result, currency)
    return result



def Send(sender, receiver, amount, currency, sender_key):

    nonce = web3.eth.getTransactionCount(sender)

    tx = {
    "nonce": nonce,
    "to": receiver,
    "value": web3.toWei(amount, "ether"),
    "gas": 70000,
    "gasPrice": web3.toWei("2000", "gwei"),
    "chainId": 4690,

    }

    sign_txn = web3.eth.account.signTransaction(tx, private_key=sender_key)
    tx_hash = web3.eth.sendRawTransaction(sign_txn.rawTransaction)

    return str(tx_hash.hex())





def SendK(sender, receiver, amount, currency, sender_key):
    amount = amount * 1000000000000000000

    transaction = contract.functions.transfer(receiver, amount).buildTransaction({'chainId': 4690, 'gas':70000, 'nonce': web3.eth.getTransactionCount(sender)})

    sign_txn = web3.eth.account.signTransaction(transaction, private_key=sender_key)
    tx_hash = web3.eth.sendRawTransaction(sign_txn.rawTransaction)

    return tx_hash.hex()
